import React, { Component } from 'react';
import './App.css';
import './antd.css';
import {
  BrowserRouter,
  Route,
  Link,
  Router,
  Switch,
  Redirect,
  withRouter
} from 'react-router-dom'
import { Button, Layout, Menu, Icon, Breadcrumb, Row, Col, Avatar, Dropdown, Drawer } from 'antd';
import HomeScreen from './containers/HomeScreen';
import Page2 from './containers/Page2';

const { Header, Sider, Content } = Layout;

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      collapsed: false,
    };
  }


  toggle = () => {
    this.setState({
      collapsed: !this.state.collapsed,
    });
  };
  onClickMenuItem = (e) => {
    const { history } = this.props;
    switch (e.key) {
      case "1":
        history.push("/");
        break;
      case "2":
        history.push("/page2")
    }
  }

  _key() {
    const pathname = this.props.history.location.pathname
    switch (pathname) {
      case "/":
        return "1";
      case "/page2":
        return "2"
    }
  }


  render() {
    const { collapsed, userInfo } = this.state;
    console.log(this.props.history.location.pathname)
    let page = <Layout >
      <Sider trigger={null} collapsible collapsed={this.state.collapsed} width= {400} >
        <div
          style={{
            margin: 10,
            flexDirection: "row",
            display: "flex",
            padding: 5
          }}
        >
          {/* <img
            src={}
            style={{ width: 35, height: 35 }}
          /> */}
          {collapsed ? (
            <div />
          ) : (
              <h3 style={{ color: "white", margin: "auto" }}>Inventory Managerment</h3>
            )}
        </div>
        <Menu theme="dark" mode="inline" onClick={this.onClickMenuItem} >
          <Menu.Item key="1">
            <Icon type="home" />
            <span>HomeScreen</span>
          </Menu.Item>
          <Menu.Item key="2">
            <Icon type="key" />
            <span>Page 2</span>
          </Menu.Item>
          <Menu.Item key="3">
            <Icon type="api" />
            <span>Page 3</span>
          </Menu.Item>
          <Menu.Item key="4">
            <Icon type="dollar" />
            <span>Page 4</span>
          </Menu.Item>
          <Menu.Item key="5">
            <Icon type="mail" />
            <span>Page 5</span>
          </Menu.Item>
        </Menu>
      </Sider>
      <Layout>
        <Header
          style={{
            background: "#fff",
            padding: 20,
            display: "flex",
            flexDirection: "row"
          }}
        >
          <div
            style={{ display: "flex", flexDirection: "row", width: "50%" }}
          >
            <Icon
              style={{ fontSize: 20, marginRight: 10 }}
              type={this.state.collapsed ? "menu-unfold" : "menu-fold"}
              onClick={this.toggle}
            />
            {/* <BreadcrumbCustom /> */}
          </div>
          <div
            style={{
              display: "flex",
              flexDirection: "row-reverse",
              width: "50%"
            }}
          >
            {/* <Dropdown
                style={{ display: "flex", flexDirection: "row-reverse" }}
                overlay={menuAuthen}
                placement="bottomCenter"
              >
                <a
                  style={{
                    display: "flex",
                    flexDirection: "row-reverse",
                    color: "rgba(0, 0, 0, 0.65)"
                  }}
                >
                  <Avatar icon="user" />
                  <p style={{ marginTop: -17, marginRight: 10 }}>{userInfo ? userInfo.name : "Chưa đăng nhập"}</p>
                </a>
              </Dropdown> */}
          </div>
        </Header>

        <Content
          style={{
            margin: "24px 16px",
            padding: 24,
            background: "#fff",
            minHeight: "calc(100vh - 50px)"
          }}
        >

          <Switch>
            <Route exact path="/" component={HomeScreen} />
            <Route path="/page2" component={Page2} />
          </Switch>

        </Content>

      </Layout>

      {/* <Drawer
          title="Cài đặt tài khoản"
          width={320}
          closable={false}
          onClose={() => { this.setState({ openAccountSetting: false }) }}
          visible={this.state.openAccountSetting}
        >
          <AccountSettingsComponent closeAccountSetting={() => { this.setState({ openAccountSetting: false }) }} />
        </Drawer> */}

    </Layout >

    return (
      page
    )
  }
}


export default withRouter(App);
