import React, { Component } from 'react';
import { Button, Layout, Menu, Icon, Breadcrumb, Row, Col, Input } from 'antd';
import {
    BrowserRouter,
    Route,
    Link,
    Router,
    Switch,
    withRouter
} from 'react-router-dom'


import { Select } from 'antd';

const { Option } = Select;




class FindProductScreen extends Component {

    constructor(props) {
        super(props);
        this.state = {
            keyword: '',

        }
    }

    render() {
        return (
            <div style={styles.container}>
                <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center', height: 50 }}>
                    <text style={{ fontSize: 20, fontWeight: 'bold' }}> Tìm kiếm theo : </text>
                    <Select defaultValue={this.state.day} style={{ width: 120, marginLeft: 20 }} onChange={(value) => this.handleChange(value)} size={'small'} >
                        <Option value={7}>7 ngày</Option>
                        <Option value={14}>14 ngày</Option>
                        <Option value={30}>
                            30 ngày
                    </Option>
                        <Option value={90}>90 ngày</Option>
                    </Select>
                </div>
                <Input
                    style={{ width: 500, height: 50 }}
                    value={this.state.keyword}
                    placeholder="Nhập thông tin tìm kiếm"
                    onChange={(e) => this.setState({ keyword: e.target.value })} />
                <Button type="primary" style={{ height: 50, marginLeft: 20, width: 150 }}>
                    Tìm kiếm
                    </Button>
                <Button type="primary" style={{ height: 50, marginLeft: 20, width: 200 }}>
                    Hiển thị tất cả
                    </Button>
            </div>
        )
    }
}

export default withRouter(FindProductScreen);

const styles = {
    container: {
        height: '100%',
        width: '100%',
        // display: 'flex',
        // justifyContent: 'center',
        // alignItems: 'center',
        overflow: 'auto'
    },
    container2: {
        height: '100%',
        width: '100%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: 20
    }
}
