import React, { Component } from 'react';
import { Button, Layout, Menu, Icon, Breadcrumb, Row, Col, Input } from 'antd';
import {
    BrowserRouter,
    Route,
    Link,
    Router,
    Switch,
    withRouter
} from 'react-router-dom'


import { Select } from 'antd';

const { Option } = Select;




class FindProductScreen extends Component {

    constructor(props) {
        super(props);
        this.state = {
            keyword: ''
        }
    }

    render() {
        return (
            <div style={styles.container}>
                <Input
                    style={{ width: 500, height: 50 }}
                    value={this.state.keyword}
                    placeholder="Nhập thông tin tìm kiếm"
                    onChange={(e) => this.setState({ keyword: e.target.value })} />
                <Button type="primary" style={{ height: 50, marginLeft: 20, padding: 10 }}>
                    Tìm kiếm
                    </Button>
                <Button type="primary" style={{ height: 50, marginLeft: 20 }}>
                    Hiển thị tất cả
                    </Button>
            </div>
        )
    }
}

export default withRouter(FindProductScreen);

const styles = {
    container: {
        height: '100%',
        width: '100%',
        // display: 'flex',
        // justifyContent: 'center',
        // alignItems: 'center',
        overflow: 'auto'
    },
    container2: {
        height: '100%',
        width: '100%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: 20
    }
}
